INSERT INTO `{prefix}_index_detail` (`id`, `module_id`, `language`, `topic`, `description`, `detail`, `keywords`, `relate`) VALUES ('1','1','','GCMS Ajax CMS','หน้าแรกของ GCMS, ข้อตกลงการใช้งาน','<div class="ggrid homepage">\r\n<div class="float-left block8 subcontent">\r\n<div class="bdr_content">\r\n<h2>ข้อกำหนด การนำไปใช้งาน</h2>\r\nผู้ที่ต้องการนำสคริปต์ไปใช้งาน แนะนำให้อ่านก่อนนะครับ โดยผมอนุญาติให้นำสคริปต์ ไปใช้งานได้ โดยมีข้อแม้ว่า\r\n\r\n<ol>\r\n	<li>การนำสคริปต์ไปใช้งาน ต้องทำลิงค์กลับมายังเว็บไซต์ผมในทุกๆหน้า ย้ำว่าลิงค์นะครับ จะเป็นข้อความ หรือรูปภาพก็ได้ และต้องสามารถมองเห็นและเข้าถึงได้ทั้งคนและ Search Engine คำแนะนำคือใส่ไว้ในส่วน footer ของ template แหละครับ (หลายคน นำไปใช้แล้ว ไปบอกว่าเป็นของตัวเอง) ไม่ว่าคุณจะไปพัฒนาหรือปรับแต่งอย่างไรต่อก็ตาม ขอให้เกียรติกันบ้าง</li>\r\n	<li>ห้ามนำไปขายต่อ หรือใช้กับบุคลที่สาม การนำไปขายต่อ จะต้องแจ้งให้ผมทราบ และจ่ายค่านำไปใช้งาน ต่อ 1 ชิ้น (ไม่มีการเหมาจ่ายนะครับ เอาไปใช้ 1 เว็บจ่ายให้ผมแค่ 1 เว็บ)</li>\r\n	<li>สามารถนำไปเผยแหร่ หรือ พัฒนาต่อได้ ไม่ห้ามหวง โดยผมแนะนำว่า ให้ทำลิงค์กลับมาดาวน์โหลดบนเว็บไซต์ผม เนื่องจาก หากมีการอัปเกรด จะสามารถหาได้จากที่ผมที่เดียว โดยการนำไปเผยแพร่ ให้ใช้กฏเดียวกันกับที่ใช้บนเว็บไซต์นี้</li>\r\n	<li>ขอความร่วมมือในการช่วยเหลือค่าใช้จ่ายในการจัดทำของผม (ไม่จำกัดจำนวนเงิน) เพื่อให้ผมได้มีเวลาในการพัฒนาต่ออย่างต่อเนื่อง และเป็นการบอกแก่ชาวโลกด้วยว่า คนไทยก็รู้จักเห็นค่าความรู้คนอื่นเช่นกัน</li>\r\n	<li>มีข้อสงสัย ใดๆ หรือ อยากให้ผมช่วยเหลืออย่างไร หรืออยากให้ผมพัฒนาอะไรเพิ่มเติม แจ้งได้ที่เว็บบอร์ด ถ้าไม่เหลือบ่ากว่าแรงผมจะจัดทำให้ หรือ จะว่าจ้างให้ผมจัดทำให้ตามความต้องการก็ไม่ขัดนะครับ</li>\r\n	<li>พัฒนาโมดูล วิดเจ็ท และ Theme ที่สามารถใช้ร่วมกับ GCMS ได้ และอยากเผยแพร่ต่อ ส่งมาให้ผมเพื่อเผยแพร่ให้ได้นะครับ</li>\r\n</ol>\r\n</div>\r\n\r\n<section class="widget widget_bdr document news">\r\n<header class="bar1">\r\n<h2>ข่าวประชาสัมพันธ์</h2>\r\n</header>\r\n\r\n<div class="bg">{WIDGET_DOCUMENT_news}</div>\r\n\r\n<p class="right"><a class="icon-next" href="news.html" rel="nofollow">อ่านต่อ</a></p>\r\n</section>\r\n\r\n<section class="widget widget_bdr document knowledge">\r\n<header class="bar2">\r\n<h2>ข่าวสารทั่วไป</h2>\r\n</header>\r\n\r\n<div class="bg">{WIDGET_DOCUMENT_knowledge}</div>\r\n\r\n<p class="right"><a class="icon-next" href="knowledge.html" rel="nofollow">อ่านต่อ</a></p>\r\n</section>\r\n\r\n<section class="widget widget_bdr download">\r\n<header class="bar2">\r\n<h2>ดาวน์โหลด</h2>\r\n</header>\r\n\r\n<div class="bg">{WIDGET_DOWNLOAD_download}</div>\r\n\r\n<p class="right"><a class="icon-next" href="download.html" rel="nofollow">ดูทั้งหมด</a></p>\r\n</section>\r\n\r\n<section class="widget widget_bdr board">\r\n<header class="bartop">\r\n<h2>กระดานข่าว</h2>\r\n</header>\r\n\r\n<div class="bg">{WIDGET_BOARD_forum}</div>\r\n\r\n<p class="right"><a class="icon-next" href="forum.html" rel="nofollow">อ่านต่อ</a></p>\r\n</section>\r\n\r\n<section class="widget widget_bdr gallery">\r\n<header class="bar2">\r\n<h2>Gallery</h2>\r\n</header>\r\n\r\n<div class="bg" id="rss-entertain">Loading...</div>\r\n</section>\r\n\r\n<section class="widget" id="rss_widget">{WIDGET_RSS}</section>\r\n</div>\r\n\r\n<aside class="sidebar block4 float-right sidebar_bg_color">\r\n<section class="widget widget_bdr personnel">\r\n<header>\r\n<h2>ผู้บริหาร</h2>\r\n</header>\r\n\r\n<div class="widget_bg">{WIDGET_PERSONNEL_1_menu}</div>\r\n</section>\r\n\r\n<section class="widget widget_bdr counter">\r\n<header>\r\n<h2>Counter</h2>\r\n</header>\r\n\r\n<div class="widget_bg">{WIDGET_COUNTER}</div>\r\n</section>\r\n</aside>\r\n</div>\r\n<script>\r\nnew GRSS(\'{WEBURL}/gallery.rss\').show(\'rss-entertain\',0);\r\n</script>','GCMS Ajax CMS','');
